/*
	This program will print its own source when run.
*/

void	print_content(FILE *f, char *av)
{
	int	i = 0;
	char	c;

	if (strcmp(av, "./Sully") == 0)
		c = 48 + X;
	else
		c = 48 + X - 1;
	char str[12] = {'i','n','t','\t','X', ' ', '=', ' ', c, ';', 0, 0};
	int	prog[] = PROG;
	
	fprintf(f, "%s\n#define PROG {", str);
	while (prog[i] != 0)
	{
		fprintf(f, "%d, ", prog[i]);
		i++;
	}
	fprintf(f, "0}\n");
	i = 27;
	while (prog[i] != 0)
	{
		fprintf(f, "%c", prog[i]);
		i++;
	}
}

